    txn: PersistenceTransaction,
    sessionToken: ByteString
  ): PersistencePromise<void> {
    const globals = this.globalsStore(txn);
    return globals.put({
      name: 'sessionToken',
      value: sessionToken.toUint8Array()
    });
  }
